{"settings":"{
  "vim.easymotion": true,
  "vim.normalModeKeyBindingsNonRecursive": [
    // File Utils
    {
      "before": ["<leader>", "n", "f"],
      "commands": ["fileutils.newFile"]
    },
    {
      "before": ["<leader>", "n", "d"],
      "commands": ["fileutils.newFolder"]
    },
    {
      "before": ["<leader>", "f", "n"],
      "commands": ["fileutils.renameFile"]
    },
    // Extension: Bookmarks
    {
      "before": ["<leader>", "m"],
      "commands": ["bookmarks.toggle"]
    },
    {
      "before": ["L"],
      "commands": ["bookmarks.jumpToNext"]
    },
    {
      "before": ["H"],
      "commands": ["bookmarks.jumpToPrevious"]
    },
    {
      "before": ["m", "a"],
      "commands": ["bookmarks.list"]
    },
    {
      "before": ["m", "c", "c"],
      "commands": ["bookmarks.clear"]
    },
    // 句首，句尾跳转
    {
      "before": ["g", "h"],
      "after": ["^"]
    },
    {
      "before": ["g", "e"],
      "after": ["$"]
    },
    // 控制状态栏可见性
    {
      "before": ["<leader>", "<cr>", "<cr>"],
      "commands": [
        "workbench.action.toggleStatusbarVisibility",
        "workbench.action.toggleActivityBarVisibility",
        "editor.action.toggleMinimap",
        "workbench.action.toggleFullScreen"
      ]
    },
    // 快速上下行
    {
      "before": ["J"],
      "after": ["5", "j"]
    },
    {
      "before": ["K"],
      "after": ["5", "k"]
    },
    // 检索
    {
      "before": ["F"],
      "commands": ["actions.find"]
    },
    {
      "before": ["<leader>", "<cr>"],
      "commands": [":nohlsearch"]
    },
    {
      "before": ["-"],
      "after": ["N"]
    },
    {
      "before": ["="],
      "after": ["n"]
    },
    // 格式化文件
    {
      "before": ["<leader>", "f", "m"],
      "commands": ["editor.action.formatDocument"]
    },
    // 当前标签切换
    {
      "before": ["t", "l"],
      "commands": ["workbench.action.nextEditor"]
    },
    {
      "before": ["t", "h"],
      "commands": ["workbench.action.previousEditor"]
    },
    // 侧边栏可见行
    {
      "before": ["t", "t"],
      "commands": ["workbench.action.toggleSidebarVisibility"]
    },
    {
      "before": ["g", "d"],
      "commands": ["editor.action.revealDefinition"]
    },
    {
      "before": ["N", "N"],
      "after": ["<esc>"]
    },
    // 移动当前行
    {
      "before": ["m", "j"],
      "commands": ["editor.action.moveLinesDownAction"]
    },
    {
      "before": ["m", "k"],
      "commands": ["editor.action.moveLinesUpAction"]
    },
    // 进入禅模式
    {
      "before": ["<leader>", "z", "z"],
      "commands": ["workbench.action.toggleZenMode"]
    },
    // 显示悬停
    {
      "before": ["`"],
      "commands": ["editor.action.showHover"]
    },
    // 重命名
    {
      "before": ["<leader>", "r", "n"],
      "commands": ["editor.action.rename"]
    },
    // 重做
    {
      "before": ["U"],
      "after": ["<C-r>"]
    },
    // 速览定义
    {
      "before": ["s", "d"],
      "commands": ["editor.action.peekDefinition"]
    },
    // 折叠代码块
    // {
    //   "before": ["<tab>"],
    //   "commands": ["editor.toggleFold"]
    // },
    // 多窗口
    {
      "before": ["<leader>", "w", "l"],
      "commands": ["workbench.action.splitEditorRight"]
    },
    {
      "before": ["<leader>", "w", "h"],
      "commands": ["workbench.action.splitEditorLeft"]
    },
    {
      "before": ["<leader>", "w", "k"],
      "commands": ["workbench.action.splitEditorUp"]
    },
    {
      "before": ["<leader>", "w", "j"],
      "commands": ["workbench.action.splitEditorDown"]
    },
    {
      "before": ["<leader>", "h"],
      "commands": ["workbench.action.focusLeftGroup"]
    },
    {
      "before": ["<leader>", "w", "w"],
      "commands": ["workbench.action.toggleEditorWidths"]
    },
    {
      "before": ["<leader>", "l"],
      "commands": ["workbench.action.focusRightGroup"]
    },
    {
      "before": ["<leader>", "j"],
      "commands": ["workbench.action.focusBelowGroup"]
    },
    {
      "before": ["<leader>", "k"],
      "commands": ["workbench.action.focusAboveGroup"]
    },
    // 打开、关闭状态栏
    {
      "before": ["g", "f"],
      "commands": ["workbench.action.quickOpen"]
    },
    {
      "before": [";"],
      "commands": ["workbench.action.showCommands"]
    },
    // easy motion
    {
      "before": ["s"],
      "after": ["<leader>", "<leader>", "s"]
    },
    // 选择下一个
    {
      "before": ["<leader>", "d"],
      "commands": [
        "editor.action.addSelectionToNextFindMatch",
        "editor.action.addSelectionToNextFindMatch"
      ]
    },
    {
      "before": ["N", "n"],
      "after": ["<esc>"]
    },
    {
      "before": ["N", "N"],
      "after": ["<esc>"]
    }
  ],
  "vim.leader": "<space>",
  "vim.insertModeKeyBindingsNonRecursive": [
    {
      "before": ["N", "n"],
      "after": ["<esc>"]
    },
    {
      "before": ["N", "N"],
