    },
    {
      "before": ["K"],
      "after": ["5", "k"]
    },
    {
      "before": ["N", "N"],
      "after": ["<esc>"]
    },
    {
      "before": ["/"],
      "commands": ["editor.action.commentLine"]
    },
    // 句首，句尾跳转
    {
      "before": ["g", "h"],
      "after": ["^"]
    },
    {
      "before": ["g", "e"],
      "after": ["$"]
    },
    // 选中相似的下一个
    {
      "before": ["<leader>", "d"],
      "commands": ["editor.action.addSelectionToNextFindMatch"]
    }
  ],
